                public static function
                    setCallAPIGateway (
                        mixed $varUserSession, string $varAPIWebToken, string $varAPIKey, 
                        string $varAPIVersion = null, array $varData = null, bool $varSignDisplayErrorPage = null, bool $varSignUseHTTPSProxy = null
                        )
                            {
