                                        (string) null;
                            //-----[ DATA INITIALIZATION ]-------------------------------------------------------------------(  END  )-----

                            //-----[ DATA PROCESS ]--------------------------------------------------------------------------( START )-----
                                //---> Reinitializing : varAPIVersion
                                    if (!$varAPIVersion) {
                                        $varAPIVersion =
                                            'latest';
                                        }
                                    else {
                                        $varAPIVersion =
                                            strtolower (
                                                $varAPIVersion
                                                );
                                        }

                                //---> Reinitializing : varSignDisplayErrorPage
                                    if ($varSignDisplayErrorPage === null) {
                                        $varSignDisplayErrorPage = true;
                                        }

                                //---> Reinitializing : varSignUseHTTPSProxy
                                    if ($varSignUseHTTPSProxy === null) {
                                        $varSignUseHTTPSProxy = false;
                                        }

                                //---> Reinitializing : varData
                                    if (!$varData) {
                                        $varData = [];
                                        }

                                //---> Reinitializing : varURL
                                    $varURL = 
                                        str_replace(
                                            'http:',
                                            (\App\Http\Helpers\ZhtHelper\General\Utilities\Helper_Network::isHTTPS($varUserSession) == TRUE ? 'https:' : 'http:'),
                                            'http://172.28.0.30/api/gateway'
                                            //\App\Helpers\ZhtHelper\System\Helper_Environment::getFrontEndConfigEnvironment($varUserSession, 'URL_BACKEND_API_GATEWAY')
                                            );

                                //---> Initializing : varDataArray
                                    $varDataArray = [
                                        'header' => [
                                            'authorization' => 'Bearer'.' '.$varAPIWebToken,
                                            'URL' => $varURL
                                            ],
                                        'metadata' => [
                                            'API' => [
                                                'key' => $varAPIKey,
                                                'version' => $varAPIVersion
                                                ]
                                            ],
                                        'data' => $varData
                                        ];
                                    //dd($varDataArray);
                            //-----[ DATA INITIALIZATION ]-------------------------------------------------------------------(  END  )-----

                            //-----[ DATA PROCESS ]--------------------------------------------------------------------------( START )-----
                                try {
                                    //---> Initializing : varResponseData
                                        $varResponseData =
                                             \App\Http\Helpers\ZhtHelper\General\System\Helper_HTTPResponse::getResponse(
                                                $varUserSession,
                                                $varURL,
                                                $varDataArray
                                                );
                                    
                                        //dd($varAPIVersion);
                                        $varData = (
                                            new \DateTime()
                                            );



                                    }

                                catch (\Exception $ex) {
                                    }
                            //-----[ DATA PROCESS ]--------------------------------------------------------------------------(  END  )-----

                            //-----[ DATA RETURN ]---------------------------------------------------------------------------( START )-----
                                return
                                    $varReturn;
                            //-----[ DATA RETURN ]---------------------------------------------------------------------------(  END  )-----
                            }
