                */
                public static function
                    getAllFunctionInPHPFile (
                        mixed $varUserSession, string $varFilePath
                        )
                            {
                            //---> Data Initialization
                                $varReturn = (array) null;

                            //---> Data Process
                                try {
                                    $varSource =
                                        file_get_contents (
                                            $varFilePath
                                            );

                                    $varPattern =
                                        '/'.
                                        '(((public)|(protected)|(public))?)+([\s])*+(static)?+([\s])*+(function)+([\s]*)+([a-zA-Z0-9_-]*)+([\s]*)+(\()+([\s]*)+([a-zA-Z0-9_=&$,\s\\\\]*)+([\s]*)+(\))'.
                                        '/m';

                                    preg_match_all (
                                        $varPattern,
                                        $varSource,
                                        $varArrayMatches
                                        );

                                    $varArrayData = $varArrayMatches[0];
                                    
                                    for ($i = 0, $iMax = count ($varArrayMatches[0]); $i != $iMax; $i++)
                                        {
                                        $varTemp =
                                            explode (
                                                'function',
                                                $varArrayMatches[0][$i]
                                                );
                                        $varTemp2 =
                                            explode (
                                                '(',
                                                $varTemp[1]
                                                );
                                        
                                        $varReturn[$i] = [
                                            'Scope' =>
                                                rtrim (
                                                    ltrim (
                                                        $varTemp[0]
                                                        )
                                                    ),
                                            'Name' =>
                                                preg_replace (
                                                    '/\s+/',
                                                    '',
                                                    $varTemp2[0]
                                                    ),
                                            'Parameters' =>
                                                rtrim (
                                                    ltrim (
                                                        str_replace (
                                                            ')',
                                                            '',
                                                            $varTemp2[1]
                                                            )
                                                        )
                                                    )
                                            ];

                                        $varReturn[$i]['FullFunction'] = (
                                            $varReturn[$i]['Scope'].
                                            ((strcmp($varReturn[$i]['Scope'], '') == 0) ? '' : ' ').
                                            'function '.
                                            $varReturn[$i]['Name'].
                                            ' '.
                                            ('('.$varReturn[$i]['Parameters'].')')
                                            );
                                        }
                                    }

                                catch (\Exception $ex) {
                                    }

                            //---> Data Return
                                return
                                    $varReturn;
                            }


